function toggle(s){var a="is-open";hasClass(s,a)?removeClass(s,a):addClass(s,a)}function hasClass(s,a){return s.className.match(new RegExp("(\\s|^)"+a+"(\\s|$)"))}function addClass(s,a){hasClass(s,a)||(s.className+=" "+a)}function removeClass(s,a){if(hasClass(s,a)){var e=new RegExp("(\\s|^)"+a+"(\\s|$)");s.className=s.className.replace(e," ")}}